mapper.select:


     and dpa.purchase_bill_no like concat('%', #{purchaseBillNo}, '%')
     and dpa.purchase_bill_date = #{purchaseBillDate}
     and dpa.warehouse_id = #{warehouseId}
     and dpa.department_id = #{departmentId}
     and dpa.user_id = #{userId}
     and dpa.purchase_bill_status = #{purchaseBillStatus}
     and dpa.urgency_level = #{urgencyLevel}
     and dpa.expected_delivery_date = #{expectedDeliveryDate}
    and dpa.del_flag = 0

order by dpa.create_time desc

mapper.update:
update dep_purchase_apply
purchase_bill_no = #{purchaseBillNo}, purchase_bill_date = #{purchaseBillDate}, warehouse_id = #{warehouseId}, department_id = #{departmentId}, user_id = #{userId}, purchase_bill_status = #{purchaseBillStatus}, total_amount = #{totalAmount}, urgency_level = #{urgencyLevel}, expected_delivery_date = #{expectedDeliveryDate}, del_flag = #{delFlag}, create_by = #{createBy}, create_time = #{createTime}, update_by = #{updateBy}, update_time = #{updateTime}, remark = #{remark},
where id = #{id}
