mapper.select:

where stk.del_flag != 1
and stk.department_id is not null
and (stk.warehouse_id is null or stk.warehouse_id = 0)
 and stk.stock_no like concat('%', #{stockNo}, '%')
 and stk.suppler_id = #{supplerId}
 and stk.stock_date = #{stockDate}
 and stk.department_id = #{departmentId}
 and stk.stock_status = #{stockStatus}
 and stk.stock_type = #{stockType}
 and stk.audit_date = #{auditDate}
 and stk.stock_date >= #{beginDate}
 and stk.stock_date <= #{endDate}
order by stk.stock_date desc, stk.stock_no desc

mapper.update:
update stk_io_stocktaking
stock_no = #{stockNo}, suppler_id = #{supplerId}, stock_date = #{stockDate}, warehouse_id = #{warehouseId}, department_id = #{departmentId}, stock_status = #{stockStatus}, user_id = #{userId}, stock_type = #{stockType}, del_flag = #{delFlag}, audit_date = #{auditDate}, reject_reason = #{rejectReason}, create_by = #{createBy}, create_time = #{createTime}, update_by = #{updateBy}, update_time = #{updateTime}, remark = #{remark}, is_month_init = #{isMonthInit},
where id = #{id}
