mapper.select:


     and npa.apply_no like concat('%', #{applyNo}, '%')
     and npa.apply_date = #{applyDate}
     and npa.department_id = #{departmentId}
     and npa.apply_status = #{applyStatus}

        and date_format(npa.apply_date,'%y%m%d') >= date_format(#{beginDate},'%y%m%d')


        and date_format(npa.apply_date,'%y%m%d') <= date_format(#{endDate},'%y%m%d')

    and npa.del_flag = 0

order by npa.create_time desc

mapper.update:
update new_product_apply
apply_no = #{applyNo}, apply_date = #{applyDate}, department_id = #{departmentId}, apply_status = #{applyStatus}, total_amount = #{totalAmount}, del_flag = #{delFlag}, audit_date = #{auditDate}, create_by = #{createBy}, create_time = #{createTime}, update_by = #{updateBy}, update_time = #{updateTime}, remark = #{remark}, reason_and_benefit = #{reasonAndBenefit}, medical_insurance = #{medicalInsurance}, centralized_procurement = #{centralizedProcurement}, procurement_form = #{procurementForm}, platform_price = #{platformPrice},
where id = #{id}
