mapper.select:

where stk.qty > 0
 and stk.material_id = #{materialId}
 and stk.department_id = #{departmentId}
 and stk.qty = #{qty}
 and stk.unit_price = #{unitPrice}
 and stk.amt = #{amt}
 and stk.batch_no = #{batchNo}
 and stk.material_no = #{materialNo}
 and stk.material_date = #{materialDate}
 and stk.warehouse_date = #{warehouseDate}
order by stk.warehouse_date desc

mapper.update:
update stk_dep_inventory
material_id = #{materialId}, department_id = #{departmentId}, qty = #{qty}, unit_price = #{unitPrice}, amt = #{amt}, batch_no = #{batchNo}, material_no = #{materialNo}, material_date = #{materialDate}, warehouse_date = #{warehouseDate},
where id = #{id}
