mapper.select:
select gz.id, gz.order_no, gz.suppler_id, gz.order_date, gz.warehouse_id, NULL as department_id, gz.order_status, gz.order_type, gz.del_flag, gz.audit_date, NULL as is_follow_flag, gz.create_by, gz.create_time, gz.update_by, gz.update_time, gz.remark, s.id sId,s.code supplierCode,s.name supplierName, w.id wId,w.code warehouseCode,w.name warehouseName, d.id dId,d.code departmentCode,d.name departmentName, COALESCE(SUM(gze.amt), 0) as totalAmt from gz_order gz left join fd_supplier s on gz.suppler_id = s.id left join fd_warehouse w on gz.warehouse_id = w.id left join fd_department d on NULL = d.id left join gz_order_entry gze on gze.paren_id = gz.id and gze.del_flag != 1 where gz.del_flag != 1 and gz.order_no like concat('%', #{orderNo}, '%') and gz.suppler_id = #{supplerId} and gz.order_date = #{orderDate} and gz.warehouse_id = #{warehouseId} and gz.order_status = #{orderStatus} and gz.order_type = #{orderType} and gz.audit_date = #{auditDate}
and DATE(gz.order_date) >= DATE(#{beginDate}) and DATE(gz.order_date) <= DATE(#{endDate})
group by gz.id, gz.order_no, gz.suppler_id, gz.order_date, gz.warehouse_id, gz.order_status, gz.order_type, gz.del_flag, gz.audit_date, gz.create_by, gz.create_time, gz.update_by, gz.update_time, gz.remark, s.id, s.code, s.name, w.id, w.code, w.name, d.id, d.code, d.name order by gz.order_date desc, gz.id desc
