mapper.select:


    t.del_flag = '0'
     and t.trace_no = #{traceNo}
     and t.medical_record_no = #{medicalRecordNo}
     and t.patient_name like concat('%', #{patientName}, '%')
     and t.hospital_number = #{hospitalNumber}
     and t.order_status = #{orderStatus}
     and t.apply_dept_id = #{applyDeptId}
     and t.exec_dept_id = #{execDeptId}

order by t.create_time desc

mapper.update:
update gz_traceability
trace_no = #{traceNo}, medical_record_no = #{medicalRecordNo}, patient_name = #{patientName}, patient_sex = #{patientSex}, patient_age = #{patientAge}, hospital_number = #{hospitalNumber}, ward = #{ward}, ward_no = #{wardNo}, bed_no = #{bedNo}, apply_dept_id = #{applyDeptId}, exec_dept_id = #{execDeptId}, hospital_date = #{hospitalDate}, contact_phone = #{contactPhone}, contact_address = #{contactAddress}, chief_surgeon = #{chiefSurgeon}, surgery_date = #{surgeryDate}, surgery_name = #{surgeryName}, admission_diagnosis = #{admissionDiagnosis}, surgery_id = #{surgeryId}, remark = #{remark}, order_status = #{orderStatus}, audit_date = #{auditDate}, audit_by = #{auditBy}, update_by = #{updateBy}, update_time = #{updateTime},
where id = #{id}

mapper.select:
select te.id as entry_id, te.parent_id as entry_parent_id, te.material_id as entry_material_id, te.inventory_id as entry_inventory_id, te.material_name as entry_material_name, te.specification as entry_specification, te.model as entry_model, te.unit as entry_unit, te.quantity as entry_quantity, te.charge_price as entry_charge_price, te.batch_no as entry_batch_no, te.expiry_date as entry_expiry_date, te.in_hospital_code as entry_in_hospital_code, te.manufacturer as entry_manufacturer, te.supplier as entry_supplier, te.certificate_no as entry_certificate_no, te.billing_follow as entry_billing_follow, te.del_flag as entry_del_flag, t.order_status as parent_order_status, t.patient_name, t.patient_sex, t.chief_surgeon, t.admission_diagnosis as surgical_diagnosis, t.surgery_date as billing_time, t.create_by as scan_user, t.create_time as scan_date, m.id as mId, m.code as materialCode, m.name as materialName, m.speci as materialSpeci, m.model as materialModel, gdi.material_date as begin_time, gdi.material_no as batch_number, gdi.material_date as material_date, gdi.material_no as material_no from gz_traceability_entry te left join gz_traceability t on te.parent_id = t.id left join fd_material m on te.material_id = m.id left join gz_dep_inventory gdi on te.inventory_id = gdi.id where te.del_flag = '0' and t.del_flag = '0'
and t.order_status = #{orderStatus}
and t.trace_no = #{traceNo} and t.patient_name like concat('%', #{patientName}, '%') and t.hospital_number = #{hospitalNumber} and t.apply_dept_id = #{applyDeptId} and t.exec_dept_id = #{execDeptId} order by t.create_time desc, te.id desc
