mapper.select:

where stk.qty > 0
 and stk.material_id = #{materialId}
 and stk.warehouse_id = #{warehouseId} 
 and stk.unit_price = #{unitPrice}
 and stk.amt = #{amt}
 and stk.qty = #{qty}
 and stk.batch_no = #{batchNo}
 and stk.material_no = #{materialNo}
 and stk.material_date = #{materialDate}
 and stk.warehouse_date = #{warehouseDate}
 and w.name like concat('%', #{warehouseName}, '%')
 and m.name like concat('%', #{materialName}, '%')
 and stk.supplier_id = #{supplierId} 
order by stk.warehouse_date desc


where stk.qty > 0
 and stk.material_id = #{materialId}
 and stk.warehouse_id = #{warehouseId} 
 and stk.unit_price = #{unitPrice}
 and stk.amt = #{amt}
 and stk.qty = #{qty}
 and stk.batch_no = #{batchNo}
 and stk.material_no = #{materialNo}
 and stk.material_date = #{materialDate}
 and stk.warehouse_date = #{warehouseDate}
 and w.name like concat('%', #{warehouseName}, '%')
 and m.name like concat('%', #{materialName}, '%')
 and stk.supplier_id = #{supplierId} 
order by stk.warehouse_date desc

select stk.id, m.code materialCode,m.name materialName,sum(stk.qty) materialQty,m.speci materialSpeci,
        m.model materialModel,u.unit_name unitName,stk.material_id,stk.unit_price unitPrice,
        ROUND(sum(stk.qty) * stk.unit_price, 2) materialAmt,
        w.name warehouseName,f.factory_name factoryName,s.name supplierName
from stk_inventory stk
left join fd_warehouse w on stk.warehouse_id = w.id
left join fd_supplier s on stk.supplier_id = s.id
left join fd_material m on stk.material_id = m.id
left join fd_unit u on u.unit_id = m.unit_id
left join fd_factory f on f.factory_id = m.factory_id
where stk.qty > 0
 and stk.material_id = #{materialId}
 and stk.warehouse_id = #{warehouseId} 
 and stk.unit_price = #{unitPrice}
 and stk.amt = #{amt}
 and stk.qty = #{qty}
 and stk.batch_no = #{batchNo}
 and stk.material_no = #{materialNo}
 and stk.material_date = #{materialDate}
 and stk.warehouse_date = #{warehouseDate}
 and w.name like concat('%', #{warehouseName}, '%')
 and m.name like concat('%', #{materialName}, '%')
 and stk.supplier_id = #{supplierId} 
GROUP BY stk.id, m.code,m.name,m.speci,
m.model,u.unit_name,stk.material_id,stk.unit_price,
w.name,f.factory_name,s.name

select SUM(IFNULL(stk.qty,0)) totalQty,SUM(IFNULL(stk.qty,0) * IFNULL(stk.unit_price,0)) totalAmt
from stk_inventory stk
left join fd_warehouse w on stk.warehouse_id = w.id
left join fd_supplier s on stk.supplier_id = s.id
left join fd_material m on stk.material_id = m.id
left join fd_unit u on u.unit_id = m.unit_id
left join fd_factory f on f.factory_id = m.factory_id
where stk.qty > 0
 and stk.material_id = #{materialId}
 and stk.warehouse_id = #{warehouseId} 
 and stk.unit_price = #{unitPrice}
 and stk.amt = #{amt}
 and stk.qty = #{qty}
 and stk.batch_no = #{batchNo}
 and stk.material_no = #{materialNo}
 and stk.material_date = #{materialDate}
 and stk.warehouse_date = #{warehouseDate}
 and w.name like concat('%', #{warehouseName}, '%')
 and m.name like concat('%', #{materialName}, '%')
 and stk.supplier_id = #{supplierId}

mapper.update:
update stk_inventory
qty = #{qty}, material_id = #{materialId}, warehouse_id = #{warehouseId}, unit_price = #{unitPrice}, amt = #{amt}, batch_no = #{batchNo}, batch_id = #{batchId}, material_no = #{materialNo}, material_date = #{materialDate}, warehouse_date = #{warehouseDate}, supplier_id = #{supplierId}, begin_time = #{beginTime}, end_time = #{endTime}, receipt_order_no = #{receiptOrderNo}, kc_no = #{kcNo},
where id = #{id}

mapper.select:
select stk.id, IFNULL(m.code, '') materialCode, IFNULL(m.name, '') materialName, IFNULL(m.speci, '') materialSpeci, IFNULL(m.model, '') materialModel, IFNULL(u.unit_name, '') unitName, IFNULL(stk.unit_price, 0) unitPrice, IFNULL(w.name, '') warehouseName, IFNULL(stk.batch_no, '') batchNo, stk.begin_time produceDate, stk.end_time expiryDate, DATEDIFF(stk.end_time, CURDATE()) daysRemaining, IFNULL(stk.qty, 0) qty, IFNULL(f.factory_name, '') factoryName, IFNULL(s.name, '') supplierName from stk_inventory stk left join fd_warehouse w on stk.warehouse_id = w.id left join fd_supplier s on stk.supplier_id = s.id left join fd_material m on stk.material_id = m.id left join fd_unit u on u.unit_id = m.unit_id left join fd_factory f on f.factory_id = m.factory_id where stk.qty > 0 and stk.end_time IS NOT NULL and (m.id IS NULL OR m.del_flag IS NULL OR m.del_flag != 1)
and DATEDIFF(stk.end_time, CURDATE()) <= #{daysToExpiry} and DATEDIFF(stk.end_time, CURDATE()) >= -#{daysToExpiry}
and DATEDIFF(stk.end_time, CURDATE()) <= 90 and DATEDIFF(stk.end_time, CURDATE()) >= -90
and stk.warehouse_id = #{warehouseId}
and (IFNULL(m.name, '') like concat('%', #{materialName}, '%') or IFNULL(m.code, '') like concat('%', #{materialName}, '%'))
and stk.supplier_id = #{supplierId}
order by stk.end_time asc, stk.id asc
