mapper.select:


    (p.del_flag is null or p.del_flag != 1)
     and p.bill_no like concat('%', #{billNo}, '%')
     and p.stocktaking_id = #{stocktakingId}
     and p.stocktaking_no like concat('%', #{stocktakingNo}, '%')
     and p.warehouse_id = #{warehouseId}
     and p.bill_status = #{billStatus}

order by p.create_time desc

mapper.update:
update stk_io_profit_loss
bill_no = #{billNo}, stocktaking_id = #{stocktakingId}, stocktaking_no = #{stocktakingNo}, warehouse_id = #{warehouseId}, bill_status = #{billStatus}, audit_by = #{auditBy}, audit_date = #{auditDate}, update_by = #{updateBy}, update_time = #{updateTime}, del_flag = #{delFlag},
where id = #{id}
